package com.online.goods.util;import android.content.Context;import android.view.Gravity;import android.widget.Toast;/** * Created by archer on 2016/1/12. * Toast 统一显示类 */public class ToastUtils {    private static Toast mToast;    public static void init(Context context) {        mToast = Toast.makeText(context, "", Toast.LENGTH_SHORT);        mToast.setGravity(Gravity.BOTTOM, 0, DensityUtils.dp2px(context, 15));    }    /**     * 短时间显示 Toast     *     * @param msg     */    public static void show(String msg) {        show(msg, Toast.LENGTH_SHORT);    }    /**     * 长时间显示 Toast     *     * @param msg     */    public static void showLong(String msg) {        show(msg, Toast.LENGTH_LONG);    }    private static void show(String msg, int duration) {        if (mToast == null) {            throw new RuntimeException("ToastUtils 未初始化");        }        mToast.setText(msg);        mToast.setDuration(duration);        mToast.show();    }}